mapper.insert:
SELECT ISEQ$$_REAL_ESTATE_SALE.NEXTVAL AS salesId FROM DUAL
INSERT INTO REAL_ESTATE_SALE ( SALES_ID, REAL_ESTATE_ID, CONTRACT_DATE, BUILDING_NAME, FLOOR, EXCLUSIVE_AREA, SALE_PRICE ) VALUES( #{salesId}, #{realEstateId}, #{contractDate, jdbcType=VARCHAR}, #{buildingName}, #{floor}, #{exclusiveArea}, #{salePrice}
)
