mapper.select:
SELECT COUNT(*) FROM REAL_ESTATE_SALE A
JOIN Real_Estate B ON A.REAL_ESTATE_ID = B.REAL_ESTATE_ID

	WHERE A.REAL_ESTATE_ID = #{realEstateId}

SELECT A.REAL_ESTATE_ID, A.NEIGHBORHOOD_ID, A.COMPLEX_NAME, A.ADDRESS, A.ADDRESS_STREET, A.CONSTRUCTION_YEAR, A.LAT, A.LNG, B.SALES_ID, B.REAL_ESTATE_ID, B.SALE_PRICE, B.CONTRACT_DATE, NVL(TO_CHAR(B.REGISTRATION_DATE, 'YYYY-MM-DD'), ' ') AS REGISTRATION_DATE, B.BUILDING_NAME, B.FLOOR, B.EXCLUSIVE_AREA From REAL_ESTATE A JOIN REAL_ESTATE_SALE B ON A.REAL_ESTATE_ID = B.REAL_ESTATE_ID JOIN NEIGHBORHOOD C ON A.NEIGHBORHOOD_ID = C.NEIGHBORHOOD_ID
A.REAL_ESTATE_ID = #{realEstateId}
ORDER BY B.CONTRACT_DATE DESC OFFSET #{offset} ROWS FETCH NEXT #{limit} ROWS ONLY

SELECT A.*, B.* FROM REAL_ESTATE_SALE A JOIN REAL_ESTATE B ON A.REAL_ESTATE_ID = B.REAL_ESTATE_ID JOIN NEIGHBORHOOD C ON B.NEIGHBORHOOD_ID = C.NEIGHBORHOOD_ID JOIN DISTRICT D ON C.DISTRICT_ID = D.DISTRICT_ID
AND D.DISTRICT_NAME = #{districtName}
AND C.NEIGHBORHOOD_NAME= #{neighborhoodName}
AND A.SALE_PRICE BETWEEN #{salePrice.min} AND (#{salePrice.max} + 1)
AND A.EXCLUSIVE_AREA BETWEEN #{exclusiveArea.min} AND (#{exclusiveArea.max} +1)
